import React, {useState} from 'react'
import {BASE_SERVER_URL, AWS_REGION, UPLOAD_TYRE_IMAGE, AWS_ACCESS_KEY, AWS_SECRET_KEY, S3_BUCKET_NAME} from '../Constants';
import axios from "axios";
import AWS from "aws-sdk";
import { useNavigate } from 'react-router-dom';
import CreateExcelPosts from '../components/CreateExcelPosts';

function Recommendations({user}) {
  const [details, setDetails] = useState(
    {size: "",
    price: "",
    status: "",
    brand: "",
    branch:"",
    quantity: "",
    product: "tyre",
    supplier: "Random",
    pattern: "",
    description: "",
    userId: user.userId,
    selectedPhoto1: null,
    selectedPhoto2: null,
    selectedPhoto3: null,
    downloadedPhoto: null
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const Recommendations = async () => {
      const url = `${BASE_SERVER_URL}/tyreposts`;
      const RecommendationsBody =
      { UserId: user.userId,
        Size: details.size,
        Price: details.price,
        Status: details.status,
        Brand: details.brand,
        Description: details.description,
        Branch: details.branch,
        Quantity: details.quantity,
        Product: details.product,
        Pattern: details.pattern,
        Supplier: details.supplier
      };
      if (user.userId == null) {
        setError("User Id unknown.");
      } else {
        setError("");
        try {
          console.log(RecommendationsBody);
          const response = await axios.post(url, RecommendationsBody );
          let postId = response.data.id;
          await uploadImageToS3(postId, details.selectedPhoto1, 1);
          await uploadImageToS3(postId, details.selectedPhoto2, 2);
          await uploadImageToS3(postId, details.selectedPhoto3, 3);
          navigate('/search-posts');
        } catch (error) {
          console.log(error);
          setError(error.response.data.toString());
        }
      }
  };

  const uploadImageToS3 = (postId, selectedPhoto, index) => {
    AWS.config.update({
      accessKeyId: AWS_ACCESS_KEY,
      secretAccessKey: AWS_SECRET_KEY,
      region: AWS_REGION
    });
    const S3 = new AWS.S3();
    //NOTE: You can change the photo name and convert to wanted type instead of doing it manually
    const objParams = {
      Bucket: S3_BUCKET_NAME,
      Key: postId + "/photo"+index+".jpeg",
      Body: selectedPhoto,
      ContentType: selectedPhoto.type // TODO: You should set content-type because AWS SDK will not automatically set file MIME
    };

    S3.putObject(objParams) //NOTE: Need retry when attempt fails.
    .promise()
    .then(res => {
     console.log(`Upload succeeded - `, res);
    })
    .catch(err => {
     console.log("Upload failed:", err);
    });
  }

  const submitHandler = e => {
      e.preventDefault();
      //Create images in S3
      Recommendations();
  }
  return (
    <div className="row">
      <div className="col-6">
        <form onSubmit={submitHandler} className="create-post-form">
          <div className="form-inner">
              {(error !== "") ? (<div className="error">{error}</div>) : ""}
              <div className="form-group">
                  <label htmlFor="size">Tyre Size:</label>
                  <input type="text" name="size" id="size" onChange={e => setDetails({...details, size: e.target.value})} value={details.size}/>
              </div>
              <div className="form-group">
                  <label htmlFor="price">Unit Price:</label>
                  <input type="text" name="price" id="price" onChange={e => setDetails({...details, price: e.target.value})} value={details.price}/>
              </div>
              <div className="form-group">
                  <label htmlFor="status">Used or New:</label>
                  <input type="text" name="status" id="status" onChange={e => setDetails({...details, status: e.target.value})} value={details.status}/>
              </div>
              <div className="form-group">
                  <label htmlFor="brand">Tyre Brand:</label>
                  <input type="text" name="brand" id="brand" onChange={e => setDetails({...details, brand: e.target.value})} value={details.brand}/>
              </div>
              <div className="form-group">
                  <label htmlFor="branch">Branch:</label>
                  <input type="text" name="branch" id="branch" onChange={e => setDetails({...details, branch: e.target.value})} value={details.branch}/>
              </div>
              <div className="form-group">
                  <label htmlFor="quantity">Quantity:</label>
                  <input type="text" name="quantity" id="quantity" onChange={e => setDetails({...details, quantity: e.target.value})} value={details.quantity}/>
              </div>
              <div className="form-group">
                  <label htmlFor="pattern">Pattern:</label>
                  <input type="text" name="pattern" id="pattern" onChange={e => setDetails({...details, pattern: e.target.value})} value={details.pattern}/>
              </div>
              <div className="form-group">
                  <label htmlFor="supplier">Supplier:</label>
                  <input type="text" name="supplier" id="supplier" onChange={e => setDetails({...details, supplier: e.target.value})} value={details.supplier}/>
              </div>
              <div className="form-group">
                  <label htmlFor="product">Product:</label>
                  <select  type="text" name="product" id="product" value={details.product} onChange={e => setDetails({...details, product: e.target.value})}>
                    <option value="tyre">tyre</option>
                    <option value="rim">rim</option>
                    <option value="mag">mag</option>
                    <option value="tube">tube</option>
                    <option value="flap">flap</option>
                  </select>
              </div>
              <div className="form-group">
                  <label htmlFor="description">Description:</label>
                  <textarea rows="5" type="text" name="description" id="description" onChange={e => setDetails({...details, description: e.target.value})} value={details.description}/>
              </div>
              <div className="form-group">
                  <label htmlFor="tyrephoto1">{UPLOAD_TYRE_IMAGE}</label>
                  <input type="file" name="tyrephoto1" alt="Photo 1" id="tyrephoto1" onChange={e => setDetails({...details, selectedPhoto1: e.target.files[0]})} />
              </div>
              <div className="form-group">
                  <label htmlFor="tyrephoto2">{UPLOAD_TYRE_IMAGE}</label>
                  <input type="file" name="tyrephoto2" alt="Photo 2" id="tyrephoto2" onChange={e => setDetails({...details, selectedPhoto2: e.target.files[0]})} />
              </div>
              <div className="form-group">
                  <label htmlFor="tyrephoto3">{UPLOAD_TYRE_IMAGE}</label>
                  <input type="file" name="tyrephoto3" alt="Photo 3" id="tyrephoto3" onChange={e => setDetails({...details, selectedPhoto3: e.target.files[0]})} />
              </div>
              <input type="submit" value="Create Post"/>
          </div>
        </form>
      </div>
      <div className="col-3 form-inner">
            <CreateExcelPosts user={user}/>
      </div>
    </div>
  )
}

export default Recommendations;
